import React, { useState, useRef, useEffect } from 'react';

const Paypal = ({ product }) => {
	const [paidFor, setPaidFor] = useState(false);
	const [error, setError] = useState(null);
	const paypalRef = useRef();
	const formEl = useRef(null);

	useEffect(() => {
		window.paypal
			.Buttons({
				createOrder: (data, actions) => {
					return actions.order.create({
						purchase_units: [
							{
								description: product.name,
								amount: {
									currency_code: 'EUR',
									value: product.price,
								},
							},
						],
					});
				},
				onApprove: async (data, actions) => {
					const order = await actions.order.capture();
					setPaidFor(true);
					console.log(order);

					if (data.orderID) {
						console.log('%c Order Successfully made!', 'color: yellow');
					}
				},
				onError: (err) => {
					setError(err);
					console.error(err);
				},
			})
			.render(paypalRef.current);
	}, [product.price, product.name]);

	if (paidFor) {
		return <div>Successfully Paid</div>;
	}

	<form
		ref={formEl}
		action="https://docs.google.com/forms/u/0/d/e/1FAIpQLSfMI1fFoie31yiktEkDHu7PKugBKSSv806pM3v9X8ZvWsrzKQ/formResponse"
	>
		<input name="entry.2092238618" hidden defaultValue="" />
		<input name="entry.1488469460" hidden defaultValue={state.email} />
		<input name="entry.1950867635" hidden defaultValue={state.phone} />
		<input name="entry.816843087" hidden defaultValue={`Anzahl Personen: ${state.person}`} />
		{
			//Hunde & Kinder
		}
		<input
			name="entry.1313324539"
			hidden
			defaultValue={
				state.child === '0' ? '' : state.child > 1 ? `${state.child} Kinder` : `${state.child} Kind`
			}
		/>
		<input
			name="entry.1525844151"
			hidden
			defaultValue={
				state.dog === '0' ? '' : state.dog > 1 ? `${state.dog} Hunde` : `${state.dog} Hund`
			}
		/>
		{
			//StartTime
		}
		<input name="entry.1195734802_hour" hidden defaultValue="10" />
		<input name="entry.1195734802_minute" hidden defaultValue="00" />
		{
			//EndTime
		}
		<input name="entry.649634804_hour" hidden defaultValue="17" />
		<input name="entry.649634804_minute" hidden defaultValue="00" />
		{
			//Location
		}
		<input name="entry.1011833342" hidden defaultValue="" />
		{
			//StartDate
		}
		<input name="entry.1981922390_day" hidden defaultValue={state.arrivalDate.getDate()} />
		<input name="entry.1981922390_month" hidden defaultValue={state.arrivalDate.getMonth() + 1} />
		<input name="entry.1981922390_year" hidden defaultValue={state.arrivalDate.getFullYear()} />
		{
			//EndDate
		}
		<input name="entry.1424445665_year" hidden defaultValue={state.departureDate.getFullYear()} />
		<input name="entry.1424445665_month" hidden defaultValue={state.departureDate.getMonth() + 1} />
		<input name="entry.1424445665_day" hidden defaultValue={state.departureDate.getDate()} />
		{
			//Radio
		}
		<input name="entry.269115742" hidden defaultValue="Rot" />
	</form>;
	return (
		<div>
			<h2 style={{ paddingBottom: '10px' }}>Jetzt bezahlen um die Buchung zu bestätigen:</h2>
			{error && <div>Uh oh, an error occurred! {error.message}</div>}
			<div ref={paypalRef} />
		</div>
	);
};

export default Paypal;
