import React, { useState, useEffect } from 'react';
import { getEvents } from '../../fetch';

import BookingCalendar from 'react-booking-calendar';
import BookingForm from '../../components/form/booking-form.component';
import {
	CalendarWrapper,
	BookingContainer,
	InfoContainer,
	InfoWrapper,
	CalendarLegend,
	BoatIcon,
	PriceListContainer,
	PriceListWrapper,
	PriceListItem,
	PriceListSeason,
	PriceListHeader,
} from './hausboot-rot.styles';

const HausbootRot = () => {
	const [startDate, setStartDate] = useState([]);
	const [endDate, setEndDate] = useState([]);

	// get the range between start and end date
	const getDaysArray = function (start, end) {
		for (var arr = [], dt = new Date(start); dt <= end - 1; dt.setDate(dt.getDate() + 1)) {
			arr.push(new Date(dt));
		}
		return arr;
	};

	const getAllDates = () => {
		var arr = [];
		for (var i in startDate) {
			let dateRange = getDaysArray(new Date(startDate[i]), new Date(endDate[i]));
			arr.push(dateRange.map((date) => date));
		}
		return arr;
	};

	const AllDates = getAllDates();

	//merge arrays in one array
	const mergeDedupe = (arr) => {
		return [...new Set([].concat(...arr))];
	};

	useEffect(() => {
		const fetchData = () => {
			getEvents((events) => setStartDate(events.map((event) => event.start))); //fetch start date
			getEvents((events) => setEndDate(events.map((event) => event.end))); //fetch end date
		};

		fetchData();
	}, []);

	return (
		<div>
			<InfoContainer>
				<InfoWrapper>Information...</InfoWrapper>
			</InfoContainer>
			<PriceListContainer>
				<PriceListWrapper>
					<PriceListItem>
						<PriceListHeader>Header</PriceListHeader>
						Tagespreis
						<div className="week">
							<span>Montag bis Donnerstag</span>
							<span>Freitag bis Sonntag</span>
							<p className="week-sub">Tagespreis ab 10:30 Uhr bis n√§chsten Tag 10:00 Uhr</p>
						</div>
					</PriceListItem>
					<PriceListSeason>
						<PriceListHeader>Header</PriceListHeader>
						NebenSeason
					</PriceListSeason>
					<PriceListSeason>
						<PriceListHeader>Header</PriceListHeader>
						HaupSeason
					</PriceListSeason>
				</PriceListWrapper>
			</PriceListContainer>
			<BookingContainer>
				<CalendarWrapper>
					<CalendarLegend>
						<div className="free">Frei</div>
						<div className="booked">Belegt</div>
					</CalendarLegend>
					<BookingCalendar bookings={mergeDedupe(AllDates)} disableHistory />
					<div></div>
				</CalendarWrapper>
			</BookingContainer>
			<BookingForm hausboot="Rot" booked={mergeDedupe(AllDates)} />
		</div>
	);
};

export default HausbootRot;
