import React, { useRef, useState } from 'react';
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
import { default as RadioButton } from './forms-ui/radio-button.component';
import { default as Button } from './forms-ui/submit-button.component';
import ReactDatePicker from './forms-ui/date-picker.component';
import TextInput from './forms-ui/textfield.component';
import { default as Select } from './forms-ui/select.component';
import { Container, FormWrapper } from './booking-form.styles';

const INITIAL_FORM_STATE = {
	name: '',
	email: '',
	phone: '',
	person: '',
	child: '0',
	dog: '0',
	arrivalDate: new Date(),
	departureDate: new Date(),
	color: 'Rot',
	// termsOfService: false,
	// radio: '',
};

const FORM_VALIDATION = Yup.object().shape({
	name: Yup.string().required('Required'),
	email: Yup.string().required('Required').email('Bitte richtige Mail angeben.'),
	phone: Yup.number('Bitte eine Nummer angeben.')
		.positive('Die Nummer muss positive sein.')
		.min(10000, 'Die Nummer muss mind. 6-stellig sein.')
		.typeError('Bitte eine Nummer angeben.')
		.required('Required'),
	person: Yup.string().required('Required'),
	child: Yup.string().required('Required'),
	dog: Yup.string().required('Required'),
	arrivalDate: Yup.date().required('Required').nullable(),
	departureDate: Yup.date()
		.required('Required')
		.nullable()
		.min(Yup.ref('arrivalDate'), 'Das Abreisedatum muss höher sein als das Anreisedatum.'),
	color: Yup.string().required('Required'),
	// termsOfService: Yup.boolean()
	// 	.oneOf([true], 'The terms and conditions must be accepted.')
	// 	.required('The terms and conditions must be accepted.'),
	// radio: Yup.string().required('Please choose your boat you want to book.'),
});

const BookingForm = () => {
	const [state, setState] = useState(INITIAL_FORM_STATE);
	const formEl = useRef(null);

	return (
		<Container>
			<FormWrapper>
				<Formik
					initialValues={{
						...INITIAL_FORM_STATE,
					}}
					validationSchema={FORM_VALIDATION}
					onSubmit={(values) => {
						console.log(values);
						setState(values);
						// formEl.current.submit();
					}}
				>
					<Form>
						<TextInput name="name" label="Name" type="text" />
						<TextInput name="email" label="E-Mail" type="email" />
						<TextInput name="phone" label="Telefon" type="tel" />
						<Select name="person" options={[, 1, 2, 3, 4, 5]} label="Erwachsene" />
						<Select name="child" options={[0, 1, 2, 3, 4, 5]} label="Kinder" />
						<Select name="dog" options={[0, 1, 2, 3]} label="Hund" />
						<ReactDatePicker name="arrivalDate" name2="departureDate" />
						<RadioButton name="color" options={['Rot', 'Blau', 'Gelb']} label="Hausboot" />
						<Button>Buchen</Button>
					</Form>
				</Formik>
				<form
					ref={formEl}
					action="https://docs.google.com/forms/u/0/d/e/1FAIpQLSfMI1fFoie31yiktEkDHu7PKugBKSSv806pM3v9X8ZvWsrzKQ/formResponse"
				>
					<input name="entry.2092238618" hidden defaultValue="" />
					<input name="entry.1488469460" hidden defaultValue={state.email} />
					<input name="entry.1950867635" hidden defaultValue={state.phone} />
					<input name="entry.816843087" hidden defaultValue={`Anzahl Personen: ${state.person}`} />
					{
						//Hunde & Kinder
					}
					<input
						name="entry.1313324539"
						hidden
						defaultValue={
							state.child === '0'
								? ''
								: state.child > 1
								? `${state.child} Kinder`
								: `${state.child} Kind`
						}
					/>
					<input
						name="entry.1525844151"
						hidden
						defaultValue={
							state.dog === '0' ? '' : state.dog > 1 ? `${state.dog} Hunde` : `${state.dog} Hund`
						}
					/>
					{
						//StartTime
					}
					<input name="entry.1195734802_hour" hidden defaultValue="10" />
					<input name="entry.1195734802_minute" hidden defaultValue="00" />
					{
						//EndTime
					}
					<input name="entry.649634804_hour" hidden defaultValue="17" />
					<input name="entry.649634804_minute" hidden defaultValue="00" />
					{
						//Location
					}
					<input name="entry.1011833342" hidden defaultValue="" />
					{
						//StartDate
					}
					<input name="entry.1981922390_day" hidden defaultValue={state.arrivalDate.getDate()} />
					<input
						name="entry.1981922390_month"
						hidden
						defaultValue={state.arrivalDate.getMonth() + 1}
					/>
					<input
						name="entry.1981922390_year"
						hidden
						defaultValue={state.arrivalDate.getFullYear()}
					/>
					{
						//EndDate
					}
					<input
						name="entry.1424445665_year"
						hidden
						defaultValue={state.departureDate.getFullYear()}
					/>
					<input
						name="entry.1424445665_month"
						hidden
						defaultValue={state.departureDate.getMonth() + 1}
					/>
					<input name="entry.1424445665_day" hidden defaultValue={state.departureDate.getDate()} />
					{
						//Radio
					}
					<input name="entry.269115742" hidden defaultValue="Rot" />
				</form>
			</FormWrapper>
		</Container>
	);
};

export default BookingForm;
