import styled from 'styled-components';

export const BookingContainer = styled.div`
	display: flex;
	justify-content: center;
	padding: 20px;
	width: 100%;

	.booking-calendar {
		.header {
			background-color: ${(props) => props.theme.bookingBlue};
			.header-content {
				.icon-previous {
					cursor: pointer;
					outline: none;
					padding: 0 10px;
					&:disabled {
						cursor: default;
					}
				}
				.icon-next {
					cursor: pointer;
					outline: none;
					padding: 0 10px;
				}
				.month-label {
					cursor: default;
				}
			}
		}

		.week {
			&.names {
				.day-box .day {
				}
			}

			.day-box .day {
				background-color: ${(props) => props.theme.bookingBlue};
				&.different-month {
				}
				&.selected {
				}
				&.today {
				}
				&.booked-day {
					background-color: ${(props) => props.theme.bookingDarkBlue};
				}
				&.booked-day:before {
					/* background-color: red; */
				}
				&.booked-night:after {
					display: none;
				}
			}
		}
	}
`;

export const CalendarWrapper = styled.div`
	width: 500px;
`;

export const InfoContainer = styled.div`
	width: 100%;
	padding: 20px;
	display: flex;
	justify-content: center;
	height: 200px;
`;
export const InfoWrapper = styled.div`
	width: 80%;
`;
