import styled from 'styled-components';

export const BookingContainer = styled.div`
	display: flex;
	justify-content: center;
	padding: 20px;
	width: 100%;

	.booking-calendar {
		.header {
			background-color: ${(props) => props.theme.bookingBlue};
			.header-content {
				.icon-previous {
					cursor: pointer;
					outline: none;
					padding: 0 10px;
					&:disabled {
						cursor: default;
					}
				}
				.icon-next {
					cursor: pointer;
					outline: none;
					padding: 0 10px;
				}
				.month-label {
					cursor: default;
				}
			}
		}

		.week {
			&.names {
				.day-box .day {
				}
			}

			.day-box .day {
				background-color: ${(props) => props.theme.bookingBlue};
				&.different-month {
				}
				&.selected {
				}
				&.today {
				}
				&.booked-day {
					background-color: ${(props) => props.theme.bookingDarkBlue};
				}
				&.booked-day:before {
					/* background-color: red; */
				}
				&.booked-night:after {
					display: none;
				}
			}
		}
	}
`;

export const CalendarWrapper = styled.div`
	width: 500px;
`;
export const CalendarLegend = styled.div`
	width: 100%;
	display: flex;
	justify-content: flex-start;
	padding: 5px 0;

	.free {
		align-items: center;
		display: flex;
		padding: 0 10px;
		&:before {
			content: '';
			background-color: ${(props) => props.theme.bookingYellow};
			width: 20px;
			margin-right: 10px;
			height: 20px;
			content: '';
			display: inline-flex;
			border-radius: 7.5px;
		}
	}
	.booked {
		padding: 0 10px;
		align-items: center;
		display: flex;
		&:before {
			background-color: ${(props) => props.theme.bookingDarkYellow};
			content: '';
			width: 20px;
			margin-right: 10px;
			height: 20px;
			content: '';
			display: inline-flex;
			border-radius: 7.5px;
		}
	}
`;

export const InfoContainer = styled.div`
	width: 100%;
	padding: 20px;
	display: flex;
	justify-content: center;
	height: 200px;
`;
export const InfoWrapper = styled.div`
	display: flex;
