import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
*{
    margin: 0;
    padding: 0;
	font-family: 'Montserrat', sans-serif;
	font-weight: bold;

	.main{
		background-color: ${(props) => props.theme.darkColor};
		color: ${(props) => props.theme.color};
	}
}

`;

export const theme = {
	mode: 'Dark',
	darkColor: '#023047',
	color: '#EDF6F9',
	lightColor: '#8ecae6',
	contrastDark: '#ffb703',
	contrastColor: '#219ebc',
	contrastLight: '#fb8500',
	bookingForm: '#EDF6F9',
	bookingBlue: '#2196f3',
	bookingDarkBlue: '#0960a5',
	bookingYellow: '#ffba08',
	bookingDarkYellow: '#e85d04',
	bookingLightYellow: '#ffd66e',
	bookingRed: '#d00000',
	bookingDarkRed: '#6a040f',
	bookingLightRed: '#ff3737',
};

export const invertTheme = {
	color: '#023047',
	lightColor: '#fb8500',
	contrastDark: '#219ebc',
	contrastColor: '#ffb703',
	darkColor: '#EDF6F9',
	contrastLight: '#8ecae6',
	bookingForm: '#3e51b5',
	bookingBlue: '#2196f3',
	bookingDarkBlue: '#0960a5',
	bookingYellow: '#ffba08',
	bookingDarkYellow: '#e85d04',
	bookingLightYellow: '#ffd66e',
	bookingRed: '#d00000',
	bookingDarkRed: '#6a040f',
	bookingLightRed: '#ff3737',
};

export default GlobalStyle;
