import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Paypal = ({ product, bookingState }) => {
	const [paidFor, setPaidFor] = useState(false);
	const [error, setError] = useState(null);
	const paypalRef = useRef();
	const formEl = useRef(null);
	const history = useHistory();

	const googleForm = process.env.REACT_APP_GOOGLE_FORM_ID;
	const config = {
		cors: 'https://cors-anywhere.herokuapp.com/', // <optional> doesn't display the cors error
		formUrl: `https://docs.google.com/forms/u/0/d/e/${googleForm}/formResponse`,
	};

	const inputs = [
		//name
		{ id: 2092238618, value: '' },
		//email
		{ id: 1488469460, value: bookingState.email },
		//phone
		{ id: 1950867635, value: bookingState.phone },
		//person
		{ id: 816843087, value: `Anzahl Personen: ${bookingState.person}` },
		//child
		{
			id: 1313324539,
			value:
				bookingState.child === '0'
					? ''
					: bookingState.child > 1
					? `${bookingState.child} Kinder`
					: `${bookingState.child} Kind`,
		},
		//dog
		{
			id: 1525844151,
			value:
				bookingState.dog === '0'
					? ''
					: bookingState.dog > 1
					? `${bookingState.dog} Hunde`
					: `${bookingState.dog} Hund`,
		},
		//startTime
		{ id: '1195734802_hour', value: '10' },
		{ id: '1195734802_minute', value: '00' },
		//endTime
		{ id: '649634804_hour', value: '17' },
		{ id: '649634804_minute', value: '00' },
		//location
		{ id: 1011833342, value: 'Oranienburg' },
		//arrivalDate
		{ id: '1981922390_day', value: bookingState.arrivalDate.getDate() },
		{ id: '1981922390_month', value: bookingState.arrivalDate.getMonth() + 1 },
		{ id: '1981922390_year', value: bookingState.arrivalDate.getFullYear() },
		//departureDate
		{ id: '1424445665_day', value: bookingState.departureDate.getDate() },
		{
			id: '1424445665_month',
			value: bookingState.departureDate.getMonth() + 1,
		},
		{ id: '1424445665_year', value: bookingState.departureDate.getFullYear() },
		//hausboot
		{ id: 269115742, value: 'Rot' },
	];

	useEffect(() => {
		window.paypal
			.Buttons({
				createOrder: (data, actions) => {
					return actions.order.create({
						purchase_units: [
							{
								description: product.name,
								amount: {
									currency_code: 'EUR',
									value: product.price,
								},
							},
						],
					});
				},
				onApprove: async (data, actions) => {
					const order = await actions.order.capture();
					// formEl.current.submit();
					setPaidFor(true);
					doSubmit();
					console.log(order);

					if (data.orderID) {
						console.log('%c Order Successfully made!', 'color: yellow');
					}
				},
				onError: (err) => {
					setError(err);
					console.error(err);
				},
			})
			.render(paypalRef.current);
	}, [product.price, product.name, history]);

	const doSubmit = async () => {
		const formData = new FormData();

		inputs.map((item) => {
			return formData.append(`entry.${item.id}`, item.value);
		});

		await axios({
			url: `${config.cors}${config.formUrl}`,
			// url: `${config.formUrl}`,
			method: 'post',
			data: formData,
			responseType: 'json',
		})
			.then((res) => {
				console.log('response', res);
				history.push('/booking');
			})
			.catch((err) => {
				console.log('err', err);
			});
	};

	if (paidFor) {
		return <div>Successfully Paid</div>;
	}

	return (
		<React.Fragment>
			<div>
				<h2 style={{ paddingBottom: '10px' }}>Jetzt bezahlen um die Buchung zu best√§tigen:</h2>
				{error && <div>Uh oh, an error occurred! {error.message}</div>}
				<div ref={paypalRef} />
			</div>
			<form ref={formEl}>
				<input name="entry.2092238618" hidden defaultValue="" />
				<input name="entry.1488469460" hidden defaultValue={bookingState.email} />
				<input name="entry.1950867635" hidden defaultValue={bookingState.phone} />
				<input
					name="entry.816843087"
					hidden
					defaultValue={`Anzahl Personen: ${bookingState.person}`}
				/>
				{
					//Hunde & Kinder
				}
				<input
					name="entry.1313324539"
					hidden
					defaultValue={
						bookingState.child === '0'
							? ''
							: bookingState.child > 1
							? `${bookingState.child} Kinder`
							: `${bookingState.child} Kind`
					}
				/>
				<input
					name="entry.1525844151"
					hidden
					defaultValue={
						bookingState.dog === '0'
							? ''
							: bookingState.dog > 1
							? `${bookingState.dog} Hunde`
							: `${bookingState.dog} Hund`
					}
				/>
				{
					//StartTime
				}
				<input name="entry.1195734802_hour" hidden defaultValue="10" />
				<input name="entry.1195734802_minute" hidden defaultValue="00" />
				{
					//EndTime
				}
				<input name="entry.649634804_hour" hidden defaultValue="17" />
				<input name="entry.649634804_minute" hidden defaultValue="00" />
				{
					//Location
				}
				<input name="entry.1011833342" hidden defaultValue="" />
				{
					//StartDate
				}
				<input
					name="entry.1981922390_day"
					hidden
					defaultValue={bookingState.arrivalDate.getDate()}
				/>
				<input
					name="entry.1981922390_month"
					hidden
					defaultValue={bookingState.arrivalDate.getMonth() + 1}
				/>
				<input
					name="entry.1981922390_year"
					hidden
					defaultValue={bookingState.arrivalDate.getFullYear()}
				/>
				{
					//EndDate
				}
				<input
					name="entry.1424445665_year"
					hidden
					defaultValue={bookingState.departureDate.getFullYear()}
				/>
				<input
					name="entry.1424445665_month"
					hidden
					defaultValue={bookingState.departureDate.getMonth() + 1}
				/>
				<input
					name="entry.1424445665_day"
					hidden
					defaultValue={bookingState.departureDate.getDate()}
				/>
				{
					//Radio
				}
				<input name="entry.269115742" hidden defaultValue="Rot" />
			</form>
		</React.Fragment>
	);
};

export default Paypal;
