import styled, { keyframes } from 'styled-components';
import { ReactComponent as boatIcon } from '../../assets/house-boat-svgrepo-com.svg';

export const CardWrapper = styled.div`
	overflow: hidden;
	padding: 0 0 32px;
	margin: 48px auto 0;
	width: 300px;
	font-family: Quicksand, arial, sans-serif;
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.05), 0 0px 40px rgba(0, 0, 0, 0.08);
	border-radius: 5px;

	&:hover {
		transform: scale(1.2);
		transition: all 0.05s ease-in-out;
	}
`;

export const IconWrapper = styled.div`
	display: flex;
	justify-content: center;
	align-items: center;
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }

  to {
    transform: rotate(360deg);
  }
`;

const wave = keyframes`

  100%{ 
	transform: translateX(0px);
  }
  50% {

	transform: translateX(40px);
  }
  0% {
	transform: translateX(-40px);
  }
`;

const waveBoat = keyframes`
  100% {
	transform: translateY(0px);
  }
  50%{

	transform: rotate(-1.5deg);
  }
  0% {
	transform: translateY(-15px);
	transform: rotate(1.5deg);
  }
`;

export const BoatIcon = styled(boatIcon)`
	width: 220px;
	height: 220px;
	position: relative;
	&:hover {
		& .water,
		.water-right {
			animation: ${wave} 4s infinite alternate-reverse;
			width: 100px;
		}
		& :not(.water, .water-right, .water-clouds) {
			animation: ${waveBoat} 5s infinite alternate-reverse;
		}

		/* animation: ${rotate} infinite 20s linear; */
	}
	& .boat-front {
		fill: ${(props) =>
			props.color === 'red'
				? props.theme.bookingRed
				: props.color === 'blue'
				? props.theme.bookingBlue
				: props.theme.bookingYellow} !important;
	}
	& .boat-front-right {
		fill: ${(props) =>
			props.color === 'red'
				? props.theme.bookingDarkRed
				: props.color === 'blue'
				? props.theme.bookingDarkBlue
				: props.theme.bookingDarkYellow} !important;
	}
	& .boat-top,
	.boat-top-right {
		stroke: black;
		stroke-linejoin: round;
		stroke-width: 10px;
	}
	& .boat-bottom,
	.boat-bottom-right {
		stroke: black;
		stroke-linejoin: round;
		stroke-width: 10px;
	}
	& .boat-front,
	.boat-front-right {
		stroke: black;
		stroke-linejoin: round;
		stroke-width: 6px;
	}
	& .chimney {
		stroke: black;
		stroke-linejoin: round;
		stroke-width: 6px;
	}
`;
