import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import DarkModeToggle from 'react-dark-mode-toggle';
import DropdownItem from '../dropdown/dropdown-item.component';
import Dropdown from '../dropdown/dropdown.component';
import { useDarkMode, useDarkModeUpdate } from '../hooks/DarkModeContext';
import {
	Nav,
	NavLogo,
	NavIcon,
	NavbarContainer,
	MobileIcon,
	NavItem,
	NavMenu,
	NavLink,
	NavItemBtn,
	NavBtnLink,
	Button,
	NavDarkMode,
	BurgerIcon,
	CloseIcon,
	DropDown,
} from './navbar.styles';
import { DarkModeMobile } from './navbar.styles';

const Navbar = () => {
	const isDarkMode = useDarkMode();
	const setIsDarkMode = useDarkModeUpdate();

	const [click, setClick] = useState(false);
	const [button, setButton] = useState(true);
	const [scroll, setScroll] = useState(0);

	const handleClick = () => setClick(!click);
	const showButton = () => {
		if (window.innerWidth <= 900) {
			setButton(false);
		} else {
			setButton(true);
		}
	};

	useEffect(() => {
		document.addEventListener('scroll', () => {
			const scrollCheck = window.scrollY > 4300;
			if (scrollCheck !== scroll) {
				setScroll(scrollCheck);
			}
		});
	});

	useEffect(() => {
		showButton();
	}, []);

	window.addEventListener('resize', showButton);

	return (
		<Nav className={scroll ? 'is-hidden' : ''}>
			<NavbarContainer>
				<NavLogo to="/">
					<NavIcon />
					Teichland-Kapit√§ne
				</NavLogo>
				<MobileIcon onClick={handleClick}>{click ? <CloseIcon /> : <BurgerIcon />}</MobileIcon>
				<NavMenu conClick={handleClick} click={click}>
					<NavItem>
						<NavLink to="/">Home</NavLink>
					</NavItem>

					{button ? (
						<NavItem>
							<DropDown>
								Hausboote
								<nav className="dropdown-content">
									<ul>
										<li>
											<Link to="/hausboot-rot">Hausboot Rot</Link>
										</li>
										<li>
											<Link to="/hausboot-blau">Hausboot Blau</Link>
										</li>
										<li>
											<Link to="/hausboot-gelb">Hausboot Gelb</Link>
										</li>
									</ul>
								</nav>
							</DropDown>
						</NavItem>
					) : (
						<Dropdown>
							<DropdownItem>
								<Link to="/hausboot-rot">Hausboot Rot</Link>
							</DropdownItem>
							<DropdownItem>
								<Link to="/hausboot-blau">Hausboot Blau</Link>
							</DropdownItem>
							<DropdownItem>
								<Link to="/hausboot-gelb">Hausboot Gelb</Link>
							</DropdownItem>
						</Dropdown>
					)}

					<NavItem>
						<NavLink to="/gallery">Fotogalerie</NavLink>
					</NavItem>

					<NavItem>
						<NavLink to="/contact">Kontakt/anfahrt</NavLink>
					</NavItem>
					<NavItemBtn>
						{button ? (
							<NavBtnLink to="/booking">
								<Button primary>Buchen</Button>
							</NavBtnLink>
						) : (
							<NavBtnLink to="/booking">
								<Button fontBig primary>
									Buchen
								</Button>
							</NavBtnLink>
						)}
					</NavItemBtn>
					{button ? (
						<NavDarkMode>
							<DarkModeToggle onChange={setIsDarkMode} checked={isDarkMode} size={40} />
						</NavDarkMode>
					) : (
						<DarkModeMobile onClick={setIsDarkMode}>
							Theme
							<NavDarkMode onClick={setIsDarkMode}>
								<DarkModeToggle onChange={setIsDarkMode} checked={isDarkMode} size={40} />
							</NavDarkMode>
						</DarkModeMobile>
					)}
				</NavMenu>
			</NavbarContainer>
		</Nav>
	);
};

export default Navbar;
