import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { LayoutContainer, DisplayCard } from './card.styles';
import Convo from './convo.component';

const cards = [{ id: 0 }, { id: 1 }, { id: 2 }, { id: 3 }];

const LayoutCards = () => {
	const [selectedId, setSelectedId] = useState(null);

	const [canDrag, setCanDrag] = useState(false);
	const containerRefs = useRef([]);
	const handlePanEnd = (e, info, card) => {
		if (selectedId) {
			setSelectedId(null);
		} else {
			setCanDrag(true);
			setSelectedId(card);
		}
	};
	return (
		<div style={{ height: '200px' }}>
			<LayoutContainer>
				{cards.map((card) => (
					<motion.div
						className={selectedId === card.id ? 'opened-card' : 'card'}
						key={card.id}
						layout
						dragConstraints={{ left: canDrag ? -850 : 0, right: 0 }}
						dragElastic={0.2}
						onPanEnd={(e, info) => handlePanEnd(e, info, card)}
						ref={(el) => (containerRefs.current[card.id] = el)}
					>
						<DisplayCard hide={selectedId === card.id}>Click to see review</DisplayCard>
						{selectedId === card.id && (
							<React.Fragment>
								<Convo className="convo-div" />
							</React.Fragment>
						)}
					</motion.div>
				))}
				<motion.div className="dim-layer" animate={{ opacity: selectedId ? 0.3 : 0 }} />
			</LayoutContainer>
		</div>
	);
};
export default LayoutCards;
