import styled from 'styled-components';

export const Container = styled.div`
	display: flex;
	justify-content: center;
	padding: 20px;
	max-width: 600px;

	.booking-calendar {
		.header {
			background-color: ${(props) => props.theme.bookingBlue};
			.header-content {
				.icon-previous {
					outline: none;
					padding: 0 10px;
				}
				.icon-next {
					outline: none;
					padding: 0 10px;
				}
				.month-label {
				}
			}
		}

		.week {
			&.names {
				.day-box .day {
				}
			}

			.day-box .day {
				background-color: ${(props) => props.theme.bookingRed};
				&.different-month {
				}
				&.selected {
				}
				&.today {
				}
				&.booked-day {
					background-color: ${(props) => props.theme.bookingDarkRed};
				}
				&.booked-day:before {
					background-color: ${(props) => props.theme.bookingDarkRed};
				}
				&.booked-night:after {
					display: none;
				}
			}
		}
	}
`;

export const CalendarWrapper = styled.div`
	width: 100%;
`;
