import styled, { keyframes } from 'styled-components';
import { ReactComponent as boatIcon } from '../../assets/house-boat-svgrepo-com.svg';

export const BookingContainer = styled.div`
	display: flex;
	justify-content: center;
	padding: 20px;

	.booking-calendar {
		.header {
			background-color: ${(props) => props.theme.bookingRed};
			.header-content {
				.icon-previous {
					cursor: pointer;
					outline: none;
					padding: 0 10px;
					&:disabled {
						color: ${(props) => props.theme.bookingLightRed};
						cursor: default;
					}
				}
				.icon-next {
					cursor: pointer;
					outline: none;
					padding: 0 10px;
				}
				.month-label {
					cursor: default;
					&:disabled {
						color: ${(props) => props.theme.bookingLightRed};
					}
				}
			}
		}

		.week {
			&.names {
				.day-box .day {
					color: ${(props) => props.theme.bookingLightRed};
				}
			}
			.day-box .day {
				background-color: ${(props) => props.theme.bookingRed};
				&.different-month {
					color: ${(props) => props.theme.bookingLightRed};
				}
				&.selected {
				}
				&.today {
				}
				&.booked-day {
					background-color: ${(props) => props.theme.bookingDarkRed};
				}
				&.booked-day:before {
					background-color: ${(props) => props.theme.bookingDarkRed};
				}
				&.booked-night:after {
					display: none;
				}
			}
		}
	}
`;

export const CalendarWrapper = styled.div`
	width: 500px;
`;
export const CalendarLegend = styled.div`
	width: 100%;
	display: flex;
	justify-content: flex-start;
	padding: 5px 0;

	.free {
		align-items: center;
		display: flex;
		padding: 0 10px;
		&:before {
			content: '';
			background-color: ${(props) => props.theme.bookingRed};
			width: 20px;
			margin-right: 10px;
			height: 20px;
			content: '';
			display: inline-flex;
			border-radius: 7.5px;
		}
	}
	.booked {
		padding: 0 10px;
		align-items: center;
		display: flex;
		&:before {
			background-color: ${(props) => props.theme.bookingDarkRed};
			content: '';
			width: 20px;
			margin-right: 10px;
			height: 20px;
			content: '';
			display: inline-flex;
			border-radius: 7.5px;
		}
	}
`;

export const InfoContainer = styled.div`
	padding: 20px;
	display: flex;
	justify-content: center;
`;
export const InfoWrapper = styled.div`
	height: 200px;
	justify-content: center;
	display: flex;
	width: 80%;
`;
const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }

  to {
    transform: rotate(360deg);
  }
`;
export const BoatIcon = styled(boatIcon)`
	width: 120px;
	height: 120px;
	/* fill: red; */
	/* animation: ${rotate} infinite 20s linear; */
	& polygon {
		fill: red;
	}
`;
