import React, { useState, useEffect } from 'react';
import { getEvents } from '../../fetch';
import _ from 'lodash';

import BookingCalendar from 'react-booking-calendar';
import { CalendarWrapper, Container } from './hausboot-rot.styles';

const HausbootRot = () => {
	const [startDate, setStartDate] = useState([]);
	const [endDate, setEndDate] = useState([]);

	const getDaysArray = function (start, end) {
		for (var arr = [], dt = new Date(start); dt <= end; dt.setDate(dt.getDate() + 1)) {
			arr.push(new Date(dt));
		}
		return arr;
	};
	const dayslist = getDaysArray(new Date(startDate[1]), new Date(endDate[1]));
	// const dayslist2 = getDaysArray(new Date(startDate[0]), new Date(endDate[0]));
	// const array = dayslist.concat(dayslist2);

	const getAllDates = () => {
		var arr = [];
		for (var i = 0; i < startDate.length; i++) {
			// var arrays = getDaysArray(new Date(startDate[i], new Date(endDate[i])));
			let array1 = getDaysArray(new Date(startDate[i]), new Date(endDate[i]));
			arr.push(array1);
		}
		return arr;
	};

	const AllDates = getAllDates();

	useEffect(() => {
		const fetchData = () => {
			getEvents((events) => setStartDate(events.map((event) => event.start)));
			getEvents((events) => setEndDate(events.map((event) => event.end)));
		};

		fetchData();
	}, []);

	// const bookings = startDate.map((value) => new Date(value));
	// const end = endDate.map((value) => value);
	// const dates = Object.assign(bookings, end);

	return (
		<div>
			<Container>
				<CalendarWrapper>
					<BookingCalendar bookings={dayslist} disableHistory />
					<div>
						Test: {console.log(AllDates)}
						{
							// endDate.map((value) => console.log(value))
							// getEvents((events) => events.map((event) => console.log(event.start)))
							// console.log(dayslist)
						}
					</div>
				</CalendarWrapper>
			</Container>
		</div>
	);
};

export default HausbootRot;
