import styled from 'styled-components';
import { createElement } from 'react';
import { renderToStaticMarkup } from 'react-dom/server';
import { RiArrowLeftCircleLine } from 'react-icons/ri';

const reactSvgComponentToMarkupString = (Component) =>
	`data:image/svg+xml,${encodeURIComponent(renderToStaticMarkup(createElement(Component)))}`;

export const Container = styled.div`
	display: flex;
	justify-content: center;
	padding: 20px;
	max-width: 600px;

	.booking-calendar {
		.header {
			/* background-color: red !important; */
			.header-content {
				.icon-previous {
					color: transparent;
					outline: none;
					padding: 0 10px;
					&.icon-previous:after {
						background: url(${reactSvgComponentToMarkupString(RiArrowLeftCircleLine)});
						color: white;
					}
				}
				.icon-next {
					outline: none;
					padding: 0 10px;
				}
				.month-label {
				}
			}
		}

		.week {
			&.names {
				.day-box .day {
				}
			}

			.day-box .day {
				/* background-color: red; */
				&.different-month {
				}
				&.selected {
				}
				&.today {
				}
				&.booked-day {
					background-color: #0960a5;
				}
				&.booked-day:before {
					/* background-color: red; */
				}
				&.booked-night:after {
					display: none;
				}
			}
		}
	}
`;

export const CalendarWrapper = styled.div`
	width: 100%;
`;

export const ArrowLeft = styled(RiArrowLeftCircleLine)`
	color: white;
	font-size: 30px;
`;
