import React, { useState, useEffect } from 'react';
import { getEvents } from '../../fetch';

import BookingCalendar from 'react-booking-calendar';
import BookingForm from '../../components/form/booking-form.component';
import {
	CalendarWrapper,
	BookingContainer,
	InfoContainer,
	InfoWrapper,
	CalendarLegend,
	BoatIcon,
	PriceListContainer,
	PriceListWrapper,
	PriceListItem,
	PriceListSeason,
	PriceListHeader,
	PriceList,
} from './hausboot-rot.styles';

const HausbootRot = () => {
	const [startDate, setStartDate] = useState([]);
	const [endDate, setEndDate] = useState([]);

	// get the range between start and end date
	const getDaysArray = function (start, end) {
		for (var arr = [], dt = new Date(start); dt <= end - 1; dt.setDate(dt.getDate() + 1)) {
			arr.push(new Date(dt));
		}
		return arr;
	};

	const getAllDates = () => {
		var arr = [];
		for (var i in startDate) {
			let dateRange = getDaysArray(new Date(startDate[i]), new Date(endDate[i]));
			arr.push(dateRange.map((date) => date));
		}
		return arr;
	};

	const AllDates = getAllDates();

	//merge arrays in one array
	const mergeDedupe = (arr) => {
		return [...new Set([].concat(...arr))];
	};

	useEffect(() => {
		const fetchData = () => {
			getEvents((events) => setStartDate(events.map((event) => event.start))); //fetch start date
			getEvents((events) => setEndDate(events.map((event) => event.end))); //fetch end date
		};

		fetchData();
	}, []);

	return (
		<div>
			<InfoContainer>
				<InfoWrapper>Information...</InfoWrapper>
			</InfoContainer>
			<PriceListContainer>
				<PriceListWrapper>
					<PriceList>
						<PriceListHeader>
							<span className="price-header">Preise</span>
							<span className="season-header">
								Nebensaison
								<p className="week-sub">
									27.03.2021 - 29.052021 und 26.09.2021 - 20.05.2022 (ohne Himmelfahrt)
								</p>
							</span>
							<span className="season-header">
								Hauptsaison
								<p className="week-sub">22.05. 2021 - 25.09.2021 und 21.05.2022 - 03.09.2022</p>
							</span>
						</PriceListHeader>
						<PriceListItem>
							<div className="week">
								Tagespreis
								<p className="week-sub">Tagespreis ab 10:30 Uhr bis nächsten Tag 10:00 Uhr</p>
							</div>
							<PriceListSeason />
							<PriceListSeason />
						</PriceListItem>
						<PriceListItem>
							<div className="week">
								<span className="item">Freitag bis Sonntag</span>
							</div>
							<PriceListSeason>
								Benzin nach Verbrauch 1,90 Euro/Liter
								{
									// <h3 className="total">GesamtPreis 1</h3>
								}
							</PriceListSeason>
							<PriceListSeason>HaupSeason</PriceListSeason>
						</PriceListItem>
						<PriceListItem>
							<div className="week">
								<span className="item">Montag bis Donnerstag</span>
							</div>
							<PriceListSeason>NebenSeason</PriceListSeason>
							<PriceListSeason>HaupSeason</PriceListSeason>
						</PriceListItem>
						<PriceListItem>
							<div className="week">
								Wochenendpreis
								<p className="week-sub">Wochenendpreis ab 10:30 Uhr bis nächsten Tag 10:00 Uhr</p>
								<p className="week-sub">1 Woche ab 15 Uhr bis nächste Woche 10:00 Uhr</p>
							</div>
							<PriceListSeason />
							<PriceListSeason />
						</PriceListItem>
						<PriceListItem>
							<div className="week">
								<span className="item">Freitag bis Sonntag</span>
							</div>
							<PriceListSeason>NebenSeason</PriceListSeason>
							<PriceListSeason>HaupSeason</PriceListSeason>
						</PriceListItem>
						<PriceListItem>
							<div className="week">
								<span className="item">1 Woche von Samstag bis Samstag</span>
							</div>
							<PriceListSeason>NebenSeason</PriceListSeason>
							<PriceListSeason>HaupSeason</PriceListSeason>
						</PriceListItem>
						<PriceListItem>
							<div className="week">Zusatzkosten</div>
							<PriceListSeason />
							<PriceListSeason />
						</PriceListItem>
						<PriceListItem>
							<div className="week">
								<span className="item">1 Woche von Samstag bis Samstag</span>
							</div>
							<PriceListSeason>NebenSeason</PriceListSeason>
							<PriceListSeason>HaupSeason</PriceListSeason>
						</PriceListItem>
						<span className="item">Benzin nach Verbrauch 1,90 Euro/Liter</span>
						<span className="item">Gasflasche 2,00 Euro/Tag</span>
						<span className="item">Kugelgrill eimalig 5,00 Euro</span>
						<span className="item">Wäschepaket p.P. 20,00 Euro</span>
						<span className="item">Parkplatz (eingezäunt) 3,00 Euro/Tag</span>
						<span className="item">Endreinigung (mit Hund) 50,- (70,-) Euro</span>
						<span className="item">Fäkalientankentleerung 25,00 Euro</span>
					</PriceList>
				</PriceListWrapper>
			</PriceListContainer>
			<BookingContainer>
				<CalendarWrapper>
					<CalendarLegend>
						<div className="free">Frei</div>
						<div className="booked">Belegt</div>
					</CalendarLegend>
					<BookingCalendar bookings={mergeDedupe(AllDates)} disableHistory />
					<div></div>
				</CalendarWrapper>
			</BookingContainer>
			<BookingForm hausboot="Rot" booked={mergeDedupe(AllDates)} />
		</div>
	);
};

export default HausbootRot;
