import React, { useCallback, useState } from 'react';
import { motion, LayoutGroup } from 'framer-motion';
import { AnimatePresence } from 'framer-motion/dist/es/index';

const Card = () => {
	const [index, setIndex] = useState(false);

	const handleClose = useCallback(() => {
		setIndex(false);
	}, []);

	return (
		<LayoutGroup>
			<ul className="gallery">
				{[0, 1, 2, 3].map((item) => (
					<motion.li
						className="item"
						key={item}
						layoutId={`${item}`}
						onClick={() => index === false && setIndex(item)}
						initial={{ borderRadius: '0.6rem' }}
					>
						<motion.div className="placeholder" layoutId={`placeholder-${item}`}>
							<div />
							<div />
							<div />
						</motion.div>
						<motion.div className="additional" layoutId={`additional-${item}`}>
							<div />
							<div />
							<div />
						</motion.div>
						<motion.div className="close" layoutId={`close-${item}`} />
					</motion.li>
				))}
			</ul>
			<AnimatePresence>
				{index !== false && (
					<React.Fragment>
						<motion.div className="modalContainer" key="modal">
							<motion.div
								className="modal"
								layoutId={`${index}`}
								initial={{ borderRadius: '1.2rem' }}
							>
								<motion.div className="placeholder" layoutId={`placeholder-${index}`}>
									<div />
									<div />
									<div />
								</motion.div>
								<motion.div className="additional" layoutId={`additional-${index}`}>
									<div />
									<div />
									<div />
								</motion.div>
								<motion.div
									className="modalClose"
									layoutId={`close-${index}`}
									onClick={handleClose}
									initial={{ opacity: 0 }}
									animate={{ opacity: 1 }}
									exit={{ opacity: 0 }}
								>
									<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
										<path
											d="M15 5L5 15M5 5l5.03 5.03L15 15"
											fill="transparent"
											strokeWidth="2"
											stroke="currentColor"
											strokeLinecap="round"
										/>
									</svg>
								</motion.div>
							</motion.div>
						</motion.div>
						<motion.div
							className="modalBackdrop"
							key="backdrop"
							onClick={handleClose}
							variants={{
								hidden: {
									opacity: 0,
									transition: {
										duration: 0.16,
									},
								},
								visible: {
									opacity: 0.8,
									transition: {
										delay: 0.04,
										duration: 0.2,
									},
								},
							}}
							initial="hidden"
							exit="hidden"
							animate="visible"
						/>
					</React.Fragment>
				)}
			</AnimatePresence>
		</LayoutGroup>
	);
};

export default Card;
