import React, { useEffect, useState } from 'react';
import { motion, Transition, AnimatePresence, AnimateSharedLayout } from 'framer-motion';

const LayoutCards = () => {
	const Transition = {
		// type: 'spring',
		// stiffness: 400,
		// damping: 35,
		// mass: .5,
		duration: 2,
	};

	const Modal = ({ onClose }) => {
		const [count, setCount] = useState(0);

		return (
			<div className="version-modal-container" onClick={onClose}>
				<motion.div
					layoutId="step"
					transition={transition}
					className="version-modal"
					style={{ borderRadius: 20 }}
				>
					<button onClick={onClose}>x</button>

					<motion.div layoutId="status" className="status" transition={transition} />

					<motion.div layoutId="content" transition={transition} className="content">
						<img src="//s3.ireland.envoy.rocks/teamleader/projects/fuut-fuut/cQfKQb_image_processing20190925-709-8ogkeq.png" />
					</motion.div>
					<motion.div className="feedback">
						<strong>Feedback panel</strong>
						<br />
						(should open and close)
						<br />
						<br />
						{count}
					</motion.div>
				</motion.div>
			</div>
		);
	};

	<AnimateSharedLayout type="crossfade">
		<motion.div
			layoutId="step"
			onClick={() => setOpen(true)}
			className="step"
			style={{ borderRadius: 10 }}
		>
			<motion.div layoutId="status">
				<svg className="comments" width="28" height="28" viewBox="0 0 28 28" version="1.1">
					<symbol viewBox="0 0 24 24" id="ri-chat-3-fill">
						<g>
							<path fill="none" d="M0 0h24v24H0z" />
							<path d="M7.291 20.824L2 22l1.176-5.291A9.956 9.956 0 0 1 2 12C2 6.477 6.477 2 12 2s10 4.477 10 10-4.477 10-10 10a9.956 9.956 0 0 1-4.709-1.176z" />
						</g>
					</symbol>
					<use xlinkHref="#ri-chat-3-fill" />
					<text x="50%" y="66%" textAnchor="middle" className="count">
						3
					</text>
				</svg>
			</motion.div>
			<motion.div layoutId="content" className="content" />
		</motion.div>
		<AnimatePresence>{open && <Modal onClose={() => setOpen(false)} />}</AnimatePresence>
	</AnimateSharedLayout>;
};

export default LayoutCards;
